function formatNumber(e){return e.toLocaleString("en-US",{maximumFractionDigits:0})}function handleInput(e){const t=e.target;let n=t.value.replace(/[^\d]/g,"");if(n=n.replace(/^0+/,""),!n)return void(t.value="");const u=formatNumber(Number(n));t.value=u}document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("input");e&&e.addEventListener("input",handleInput)}));